"use server";

import {
  type ActionResponse,
  error as createErrorResponse,
  success as createSuccessResponse,
} from "@/shared/lib/action-response";
import { executeAction } from "@/shared/lib/execute-action";
import { isWithinInterval } from "date-fns";
import {
  appointmentsToCalendarEvents,
  getMockMetrics,
  MOCK_APPOINTMENTS,
  MOCK_CUSTOMERS,
  MOCK_RESOURCES,
  MOCK_SERVICES,
  MOCK_STAFF,
} from "./model/mocks";
import type {
  Appointment,
  AppointmentFilters,
  ConflictInfo,
  DateRange,
} from "./model/types";

export async function getAppointments(
  dateRange: DateRange,
  filters?: Partial<AppointmentFilters>
) {
  return executeAction(
    "getAppointments",
    async () => {
      let apps = MOCK_APPOINTMENTS.filter((a) =>
        isWithinInterval(a.startTime, {
          start: dateRange.start,
          end: dateRange.end,
        })
      );

      if (filters?.staffIds?.length)
        apps = apps.filter((a) => filters.staffIds!.includes(a.staffId));
      if (filters?.resourceIds?.length)
        apps = apps.filter(
          (a) => a.resourceId && filters.resourceIds!.includes(a.resourceId)
        );
      if (filters?.statuses?.length)
        apps = apps.filter((a) => filters.statuses!.includes(a.status));
      if (filters?.serviceIds?.length) {
        apps = apps.filter(
          (a) =>
            (a.serviceId && filters.serviceIds!.includes(a.serviceId)) ||
            a.items?.some((i) => filters.serviceIds!.includes(i.serviceId))
        );
      }
      if (filters?.searchQuery) {
        const q = filters.searchQuery.toLowerCase();
        apps = apps.filter(
          (a) =>
            a.customerName.toLowerCase().includes(q) ||
            a.customerPhone.includes(q) ||
            (a.serviceName && a.serviceName.toLowerCase().includes(q))
        );
      }
      return appointmentsToCalendarEvents(apps);
    },
    "Không thể tải danh sách lịch hẹn"
  );
}

export async function getAppointmentById(id: string) {
  return executeAction(
    "getAppointmentById",
    async () => {
      const apt = MOCK_APPOINTMENTS.find((a) => a.id === id);
      if (!apt) throw new Error("Not found");
      return apt;
    },
    "Không tìm thấy lịch hẹn"
  );
}

export async function getAppointmentMetrics(date: Date = new Date()) {
  return executeAction(
    "getAppointmentMetrics",
    async () => getMockMetrics(date),
    "Không thể tải thống kê"
  );
}

export async function createAppointment(data: {
  customerId: string;
  serviceIds: string[];
  staffId: string;
  resourceId?: string;
  startTime: Date;
  notes?: string;
  isRecurring?: boolean;
  recurrenceRule?: string;
}) {
  return executeAction(
    "createAppointment",
    async () => {
      if (!data.customerId || !data.staffId || !data.serviceIds.length)
        throw new Error("Missing fields");

      const customer = MOCK_CUSTOMERS.find((c) => c.id === data.customerId);
      const staff = MOCK_STAFF.find((s) => s.id === data.staffId);
      const service = MOCK_SERVICES.find((s) => s.id === data.serviceIds[0]);
      const resource = data.resourceId
        ? MOCK_RESOURCES.find((r) => r.id === data.resourceId)
        : undefined;
      if (!customer || !staff || !service) throw new Error("Invalid data");

      const conflicts = await checkConflictsLogic(
        data.staffId,
        data.startTime,
        new Date(data.startTime.getTime() + (service.duration || 60) * 60000)
      );
      if (conflicts.length > 0)
        throw new Error(`Xung đột lịch: ${conflicts[0].message}`);

      const bookingItems = data.serviceIds.map((sId) => {
        const s = MOCK_SERVICES.find((srv) => srv.id === sId)!;
        return {
          serviceId: sId,
          serviceName: s.name,
          price: s.price,
          duration: s.duration,
          startTime: data.startTime,
          staffId: data.staffId,
          resourceId: data.resourceId,
        };
      });

      const totalDuration = bookingItems.reduce((s, i) => s + i.duration, 0);
      const newAppointment: Appointment = {
        id: `apt-new-${Date.now()}`,
        customerId: data.customerId,
        customerName: customer.name,
        customerPhone: customer.phone,
        staffId: data.staffId,
        staffName: staff.name,
        staffAvatar: staff.avatar,
        items: bookingItems,
        totalPrice: bookingItems.reduce((s, i) => s + i.price, 0),
        totalDuration,
        serviceId: data.serviceIds[0],
        serviceName: service.name,
        serviceColor: service.color,
        resourceId: data.resourceId,
        resourceName: resource?.name,
        startTime: data.startTime,
        endTime: new Date(data.startTime.getTime() + totalDuration * 60000),
        duration: totalDuration,
        status: "PENDING",
        notes: data.notes,
        isRecurring: data.isRecurring || false,
        recurrenceRule: data.recurrenceRule,
        createdAt: new Date(),
        updatedAt: new Date(),
        createdBy: "current-user",
      };

      MOCK_APPOINTMENTS.push(newAppointment);
      return newAppointment;
    },
    "Không thể tạo lịch hẹn"
  );
}

export async function updateAppointment(
  id: string,
  data: Partial<Appointment>
) {
  return executeAction(
    "updateAppointment",
    async () => {
      const existing = MOCK_APPOINTMENTS.find((a) => a.id === id);
      if (!existing) throw new Error("Not found");
      Object.assign(existing, { ...data, updatedAt: new Date() });
      return existing;
    },
    "Không thể cập nhật lịch hẹn"
  );
}

export async function updateAppointmentTime(
  id: string,
  startTime: Date,
  endTime: Date
) {
  return executeAction(
    "updateAppointmentTime",
    async () => {
      const existing = MOCK_APPOINTMENTS.find((a) => a.id === id);
      if (!existing) throw new Error("Not found");
      if (startTime < new Date()) throw new Error("Past time");

      const conflicts = await checkConflictsLogic(
        existing.staffId,
        startTime,
        endTime,
        id
      );
      if (conflicts.length > 0)
        throw new Error(`Xung đột: ${conflicts[0].message}`);

      Object.assign(existing, {
        startTime,
        endTime,
        duration: Math.round((endTime.getTime() - startTime.getTime()) / 60000),
        updatedAt: new Date(),
      });
      return existing;
    },
    "Không thể cập nhật thời gian"
  );
}

export async function deleteAppointment(id: string) {
  return executeAction(
    "deleteAppointment",
    async () => {
      const idx = MOCK_APPOINTMENTS.findIndex((a) => a.id === id);
      if (idx === -1) throw new Error("Not found");
      // MOCK: In reality we might splice or mark deleted. For now just check existence.
      return null;
    },
    "Không thể xóa lịch hẹn"
  );
}

export async function checkInAppointment(id: string) {
  return executeAction("checkInAppointment", async () => {
    const existing = MOCK_APPOINTMENTS.find((a) => a.id === id);
    if (!existing || existing.status !== "CONFIRMED")
      throw new Error("Invalid state");
    existing.status = "IN_PROGRESS";
    existing.updatedAt = new Date();
    return existing;
  });
}

export async function markNoShow(id: string) {
  return executeAction("markNoShow", async () => {
    const existing = MOCK_APPOINTMENTS.find((a) => a.id === id);
    if (!existing || existing.status !== "CONFIRMED")
      throw new Error("Invalid state");
    existing.status = "NO_SHOW";
    existing.updatedAt = new Date();
    return existing;
  });
}

export async function cancelAppointment(id: string, reason?: string) {
  return executeAction("cancelAppointment", async () => {
    const existing = MOCK_APPOINTMENTS.find((a) => a.id === id);
    if (!existing || ["CANCELLED", "COMPLETED"].includes(existing.status))
      throw new Error("Invalid state");
    existing.status = "CANCELLED";
    existing.updatedAt = new Date();
    if (reason)
      existing.notes = `${existing.notes || ""}\n[Lý do hủy]: ${reason}`.trim();
    return existing;
  });
}

async function checkConflictsLogic(
  staffId: string,
  startTime: Date,
  endTime: Date,
  excludeId?: string
): Promise<ConflictInfo[]> {
  const overlapping = MOCK_APPOINTMENTS.filter((apt) => {
    if (
      apt.id === excludeId ||
      apt.staffId !== staffId ||
      ["CANCELLED", "NO_SHOW"].includes(apt.status)
    )
      return false;
    return apt.startTime < endTime && apt.endTime > startTime;
  });

  return overlapping.map((apt) => ({
    eventId: apt.id,
    conflictsWith: [apt.id],
    type: "overlap",
    severity: "error",
    message: `${apt.staffName} đã có hẹn "${apt.serviceName}" lúc ${apt.startTime.toLocaleTimeString("vi-VN", { hour: "2-digit", minute: "2-digit" })}`,
  }));
}

export async function checkConflicts(
  staffId: string,
  startTime: Date,
  endTime: Date,
  excludeId?: string
) {
  return executeAction(
    "checkConflicts",
    async () => checkConflictsLogic(staffId, startTime, endTime, excludeId),
    "Không thể kiểm tra xung đột"
  );
}

export async function getStaffList() {
  return executeAction(
    "getStaffList",
    async () => MOCK_STAFF.filter((s) => s.isActive),
    "Không thể tải danh sách nhân viên"
  );
}

export async function getResourceList() {
  return executeAction(
    "getResourceList",
    async () => MOCK_RESOURCES.filter((r) => r.isActive),
    "Không thể tải danh sách tài nguyên"
  );
}

export async function getServiceList() {
  return executeAction(
    "getServiceList",
    async () => MOCK_SERVICES,
    "Không thể tải danh sách dịch vụ"
  );
}

export async function searchCustomers(query: string) {
  return executeAction(
    "searchCustomers",
    async () => {
      if (!query || query.length < 2) return [];
      return MOCK_CUSTOMERS.filter(
        (c) =>
          c.name.toLowerCase().includes(query.toLowerCase()) ||
          c.phone.includes(query)
      );
    },
    "Không thể tìm kiếm khách hàng"
  );
}

"use client";

import { ReviewPrompt } from "@/features/reviews/components/review-prompt";
import {
  PageContent,
  PageHeader,
  PageShell,
  SurfaceCard,
} from "@/shared/components/layout/page-layout";
import { ActionResponse } from "@/shared/lib/action-response";
import { cn } from "@/shared/lib/utils";
import {
  Button,
  DeleteConfirmDialog,
  showToast,
  Tooltip,
  TooltipContent,
  TooltipTrigger,
} from "@/shared/ui";
import { Icon } from "@/shared/ui/custom/icon";
import {
  Activity,
  CalendarCheck,
  Clock,
  LucideIcon,
  Plus,
  RefreshCw,
  Settings2,
} from "lucide-react";
import { use, useState } from "react";
import {
  useCalendarState,
  useAppointmentMetrics,
  useAppointmentEvents,
  useAppointmentDialogs,
  useAppointmentActions,
} from "../hooks";
import { MockService } from "../model/mocks";
import type {
  AppointmentFilters,
  CalendarEvent,
  TimelineResource,
} from "../model/types";
import { CalendarView } from "./calendar";
import { AppointmentSheet } from "./sheet";
import { CancelDialog } from "./sheet/cancel-dialog";
import { DateNavigator, ViewSwitcher } from "./toolbar";
import { AppointmentsFilter } from "./toolbar/appointments-filter";
function StatBadge({
  icon: InnerIcon,
  value,
  label,
  highlight = false,
  badge = false,
}: {
  icon: LucideIcon;
  value: number | string;
  label: string;
  highlight?: boolean;
  badge?: boolean;
}) {
  return (
    <Tooltip>
      <TooltipTrigger asChild>
        <div
          className={cn(
            "flex cursor-help items-center gap-2 rounded-md border px-3 py-1.5 transition-colors",
            highlight
              ? "border-amber-200 bg-amber-50 text-amber-700 dark:border-amber-800 dark:bg-amber-950/30 dark:text-amber-500"
              : "hover:bg-muted/50 hover:border-border/50 text-muted-foreground border-transparent"
          )}
        >
          <div className="relative">
            <Icon icon={InnerIcon} />
            {badge && (
              <span className="border-background absolute -right-0.5 -top-0.5 h-2 w-2 animate-pulse rounded-full border bg-amber-500" />
            )}
          </div>
          <span className="text-sm font-semibold tabular-nums">{value}</span>
        </div>
      </TooltipTrigger>
      <TooltipContent>{label}</TooltipContent>
    </Tooltip>
  );
}

interface AppointmentsPageProps {
  appointmentsPromise: Promise<ActionResponse<CalendarEvent[]>>;
  staffListPromise: Promise<ActionResponse<TimelineResource[]>>;
  resourceListPromise: Promise<ActionResponse<TimelineResource[]>>;
  serviceListPromise: Promise<ActionResponse<MockService[]>>;

  // Dependency Injection for cross-feature actions (FSD compliance)
  createInvoiceAction: (bookingId: string) => Promise<ActionResponse<unknown>>;
  getInvoiceAction: (bookingId: string) => Promise<ActionResponse<unknown>>;
  getBookingReviewAction: (bookingId: string) => Promise<ActionResponse<unknown>>;
}

export function AppointmentsPage({
  appointmentsPromise,
  staffListPromise,
  resourceListPromise,
  serviceListPromise,
  createInvoiceAction,
  getInvoiceAction,
  getBookingReviewAction,
}: AppointmentsPageProps) {
  const appointmentsRes = use(appointmentsPromise);
  const staffRes = use(staffListPromise);
  const resourceRes = use(resourceListPromise);
  const serviceRes = use(serviceListPromise);

  const {
    view,
    setView,
    date,
    dateRange,
    formattedDateRange,
    isToday,
    goNext,
    goPrev,
    goToday,
    goToDate,
    densityMode,
  } = useCalendarState();
  const [filters, setFilters] = useState<Partial<AppointmentFilters>>({});

  const staffList = staffRes.status === "success" ? staffRes.data || [] : [];
  const roomList = resourceRes.status === "success" ? resourceRes.data || [] : [];
  const serviceList = serviceRes.status === "success" ? serviceRes.data || [] : [];

  const { metrics, refreshMetrics, isPending: isMetricsPending } = useAppointmentMetrics(date);

  const { events, refreshEvents, isPending: isEventsPending, addOptimisticEvent } = useAppointmentEvents({
    dateRange,
    filters,
    initialEvents: appointmentsRes.status === "success" ? appointmentsRes.data || [] : [],
    onRefreshMetrics: refreshMetrics,
  });

  const {
    isSheetOpen, setIsSheetOpen,
    selectedEvent, setSelectedEvent,
    sheetMode, setSheetMode,
    selectedBookingForReview, setSelectedBookingForReview,
    actionEvent, isCancelOpen, setIsCancelOpen,
    isDeleteOpen, setIsDeleteOpen,
    handleEventClick, handleCreateClick, handleEditRequest,
    handleCancelRequest, handleDeleteRequest,
  } = useAppointmentDialogs();

  const {
    isPending: isActionPending,
    isDeleting,
    isCancelling,
    handleSaveAppointment,
    handleConfirmDelete,
    handleConfirmCancel,
    handleCreateInvoice,
    handleReviewNeeded,
    onCheckIn,
    onNoShow,
  } = useAppointmentActions({
    events,
    refreshEvents,
    setSelectedBookingForReview,
    setIsSheetOpen,
    setIsDeleteOpen,
    setIsCancelOpen,
    addOptimisticEvent,
    createInvoice: createInvoiceAction,
    getInvoice: getInvoiceAction,
    getBookingReview: getBookingReviewAction,
  });

  const isRefreshing = isMetricsPending || isEventsPending || isActionPending;

  const handlePaymentSuccess = () => {
    refreshEvents();
  };

  const handleSlotClick = (clickedDate: Date, hour: number, minute: number) => {
    const startTime = new Date(clickedDate);
    startTime.setHours(hour, minute, 0, 0);
    const endTime = new Date(startTime.getTime() + 36e5);
    const staffId = filters.staffIds?.[0] || staffList[0]?.id || "";

    setSelectedEvent({
      id: "new",
      start: startTime,
      end: endTime,
      title: "Lịch hẹn mới",
      staffId,
      staffName: "",
      color: "gray",
      status: "PENDING",
      isRecurring: false,
      appointment: {
        id: "new",
        customerId: "",
        customerName: "",
        customerPhone: "",
        items: [],
        totalPrice: 0,
        totalDuration: 60,
        staffId,
        staffName: "",
        serviceId: "",
        serviceName: "",
        serviceColor: "#gray",
        startTime,
        endTime,
        duration: 60,
        status: "PENDING",
        isRecurring: false,
        createdAt: new Date(),
        updatedAt: new Date(),
        createdBy: "",
      },
    });
    setSheetMode("create");
    setIsSheetOpen(true);
  };

  if (appointmentsRes.status === "error" || staffRes.status === "error")
    return <div className="text-destructive p-4">Lỗi tải dữ liệu</div>;

  const pendingCount = metrics?.todayPending ?? 0;

  return (
    <PageShell className="h-screen overflow-hidden">
      <PageHeader>
        <div className="flex items-center gap-4">
          <DateNavigator
            date={date}
            formattedDateRange={formattedDateRange}
            isToday={isToday}
            onPrev={goPrev}
            onNext={goNext}
            onToday={goToday}
            onDateSelect={goToDate}
          />
          <div className="hidden items-center gap-2 xl:flex">
            <StatBadge
              icon={CalendarCheck}
              value={metrics?.todayTotal ?? 0}
              label="Tổng lịch hẹn hôm nay"
            />
            <StatBadge
              icon={Clock}
              value={pendingCount}
              label="Lịch hẹn chờ duyệt"
              highlight={pendingCount > 0}
              badge={pendingCount > 0}
            />
            <StatBadge
              icon={Activity}
              value={`${metrics?.occupancyRate ?? 0}%`}
              label="Công suất phục vụ"
            />
          </div>
        </div>
        <div className="flex items-center gap-1.5">
          <ViewSwitcher value={view} onChange={setView} />
          <div className="hidden items-center gap-1 pl-1 sm:flex">
            <AppointmentsFilter
              staffList={staffList}
              filters={filters}
              onFilterChange={setFilters}
            />
            <Tooltip>
              <TooltipTrigger asChild>
                <Button
                  variant="ghost"
                  size="icon"
                  onClick={refreshEvents}
                  disabled={isRefreshing}
                  className="text-muted-foreground hover:text-foreground size-8"
                >
                  <Icon
                    icon={RefreshCw}
                    className={cn(isRefreshing && "animate-spin")}
                  />
                  <span className="sr-only">Làm mới</span>
                </Button>
              </TooltipTrigger>
              <TooltipContent>Làm mới</TooltipContent>
            </Tooltip>
            <Tooltip>
              <TooltipTrigger asChild>
                <Button
                  variant="ghost"
                  size="icon"
                  className="text-muted-foreground hover:text-foreground size-8"
                >
                  <Icon icon={Settings2} />
                  <span className="sr-only">Cài đặt</span>
                </Button>
              </TooltipTrigger>
              <TooltipContent>Cài đặt hiển thị</TooltipContent>
            </Tooltip>
          </div>
          <div className="pl-1">
            <Button
              size="sm"
              onClick={handleCreateClick}
              className="h-9 px-4 shadow-sm"
            >
              <Icon icon={Plus} className="sm:mr-2" />
              <span className="hidden font-medium sm:inline">Đặt lịch</span>
            </Button>
          </div>
        </div>
      </PageHeader>

      <PageContent fullWidth className="flex flex-col gap-0 p-0">
        <div className="flex min-h-0 flex-1 flex-col p-4">
          <SurfaceCard className="flex min-h-0 flex-1 flex-col overflow-hidden rounded-lg border">
            <CalendarView
              view={view}
              date={date}
              dateRange={dateRange}
              events={events}
              densityMode={densityMode}
              staffList={staffList}
              roomList={roomList}
              onEventClick={handleEventClick}
              onSlotClick={handleSlotClick}
              isLoading={isEventsPending && events.length === 0}
              className="min-h-0 flex-1"
              onCheckIn={onCheckIn}
              onNoShow={onNoShow}
              onCancel={handleCancelRequest}
              onDelete={handleDeleteRequest}
              onEdit={handleEditRequest}
            />
          </SurfaceCard>
        </div>
      </PageContent>

      <AppointmentSheet
        open={isSheetOpen}
        onOpenChange={setIsSheetOpen}
        mode={sheetMode}
        event={selectedEvent}
        onSave={handleSaveAppointment}
        onCreateInvoice={handleCreateInvoice}
        onPaymentSuccess={handlePaymentSuccess}
        onReviewNeeded={handleReviewNeeded}
        availableStaff={staffList}
        availableResources={roomList}
        availableServices={serviceList}
      />

      {selectedEvent?.appointment &&
        selectedBookingForReview === selectedEvent.appointment.id && (
          <ReviewPrompt
            bookingId={selectedBookingForReview}
            open={!!selectedBookingForReview}
            onOpenChange={(open) => {
              if (!open) setSelectedBookingForReview(null);
            }}
            onReviewSubmitted={() => {
              setSelectedBookingForReview(null);
              refreshEvents();
            }}
            customerName={selectedEvent?.appointment?.customerName || ""}
            serviceName={selectedEvent?.appointment?.serviceName || ""}
          />
        )}

      {/* Dialogs */}
      <CancelDialog
        event={actionEvent}
        open={isCancelOpen}
        onOpenChange={setIsCancelOpen}
        onConfirm={handleConfirmCancel}
        isPending={isCancelling}
      />
      <DeleteConfirmDialog
        open={isDeleteOpen}
        onOpenChange={setIsDeleteOpen}
        onConfirm={() => handleConfirmDelete(actionEvent)}
        isDeleting={isDeleting}
        entityName="lịch hẹn"
        entityLabel={actionEvent?.title}
      />
    </PageShell>
  );
}

"use client";

import { zodResolver } from "@hookform/resolvers/zod";
import { format } from "date-fns";
import { AlertTriangle } from "lucide-react";
import { useCallback, useTransition } from "react";
import { useForm } from "react-hook-form";

import { MultiServiceSelector } from "@/features/appointments/components/selection/multi-service-selector";
import {
  Form,
  FormControl,
  FormDescription,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
  Textarea,
} from "@/shared/ui";
import { Icon } from "@/shared/ui/custom/icon";
import { Combobox } from "@/shared/ui/custom/combobox";
import { DatePicker } from "@/shared/ui/custom/date-picker";
import { TimePicker } from "@/shared/ui/custom/time-picker";

import { MockService } from "@/features/appointments/model/mocks";
import {
  quickAppointmentFormSchema,
  type QuickAppointmentFormValues,
} from "@/features/appointments/model/schemas";
import type {
  Appointment,
  TimelineResource,
} from "@/features/appointments/model/types";
import {
  useConflictCheck,
  useCustomerSearch,
} from "@/features/appointments/hooks";

// ============================================
// TYPES
// ============================================

interface AppointmentFormProps {
  id?: string;
  appointment?: Appointment | null;
  defaultValues?: {
    date?: Date;
    startTime?: string;
    staffId?: string;
  };
  onSubmit: (appointment: Appointment) => void;
  availableStaff: TimelineResource[];
  availableResources: TimelineResource[];
  availableServices: MockService[];
}

// ============================================
// COMPONENT
// ============================================

export function AppointmentForm({
  id = "appointment-form",
  appointment,
  defaultValues,
  onSubmit,
  availableStaff,
  availableResources,
  availableServices,
}: AppointmentFormProps) {
  const [isPending, startTransition] = useTransition();

  // Form
  const form = useForm<QuickAppointmentFormValues>({
    resolver: zodResolver(quickAppointmentFormSchema),
    disabled: isPending,
    defaultValues: {
      customerId: appointment?.customerId || "",
      serviceIds: appointment?.serviceId ? [appointment.serviceId] : [],
      staffId: appointment?.staffId || defaultValues?.staffId || "",
      resourceId: appointment?.resourceId || "",
      date: appointment?.startTime || defaultValues?.date || new Date(),
      startTime:
        defaultValues?.startTime ||
        (appointment ? format(appointment.startTime, "HH:mm") : "09:00"),
      notes: appointment?.notes || "",
    },
  });

  // Custom hooks - extracted logic
  const { customerOptions, isSearching, setCustomerSearch } =
    useCustomerSearch();
  const { conflicts, timeWarning, totalDuration } = useConflictCheck({
    form,
    availableServices,
    appointmentId: appointment?.id,
  });

  // ============================================
  // HANDLERS
  // ============================================

  const handleSubmit = useCallback(
    (values: QuickAppointmentFormValues) => {
      startTransition(() => {
        const [hours, minutes] = values.startTime.split(":").map(Number);
        const startTime = new Date(values.date);
        startTime.setHours(hours, minutes, 0, 0);

        // Data lookup
        const customer = customerOptions.find(
          (c) => c.id === values.customerId
        );
        const staff = availableStaff.find((s) => s.id === values.staffId);
        const service = availableServices.find(
          (s) => s.id === values.serviceIds[0]
        );
        const resource = values.resourceId
          ? availableResources.find((r) => r.id === values.resourceId)
          : undefined;

        const bookingItems = values.serviceIds.map((sId: string) => {
          const s = availableServices.find((as) => as.id === sId);
          return {
            serviceId: sId,
            serviceName: s?.name || "",
            price: s?.price || 0,
            duration: s?.duration || 0,
            startTime,
            staffId: values.staffId,
            resourceId: values.resourceId || undefined,
          };
        });

        const currentTotalDuration = bookingItems.reduce(
          (acc: number, item: any) => acc + item.duration,
          0
        );
        const currentTotalPrice = bookingItems.reduce(
          (acc: number, item: any) => acc + item.price,
          0
        );
        const finalEndTime = new Date(
          startTime.getTime() + (currentTotalDuration || 60) * 60000
        );

        const newAppointment: Appointment = {
          id: appointment?.id || `apt-new-${Date.now()}`,
          customerId: values.customerId,
          customerName: customer?.name || appointment?.customerName || "",
          customerPhone: customer?.phone || appointment?.customerPhone || "",
          items: bookingItems,
          totalPrice: currentTotalPrice,
          totalDuration: currentTotalDuration,
          serviceId: values.serviceIds[0] || "",
          serviceName: service?.name || appointment?.serviceName || "",
          serviceColor: service
            ? "#8b5cf6"
            : appointment?.serviceColor || "#8b5cf6",
          staffId: values.staffId,
          staffName: staff?.name || appointment?.staffName || "",
          resourceId: values.resourceId || undefined,
          resourceName: resource?.name || undefined,
          startTime,
          endTime: finalEndTime,
          duration: currentTotalDuration || 60,
          status: appointment?.status || "PENDING",
          notes: values.notes || "",
          internalNotes: "",
          isRecurring: false,
          createdAt: appointment?.createdAt || new Date(),
          updatedAt: new Date(),
          createdBy: "",
        };

        onSubmit(newAppointment);
      });
    },
    [
      appointment,
      availableResources,
      availableServices,
      availableStaff,
      customerOptions,
      onSubmit,
    ]
  );

  // ============================================
  // RENDER
  // ============================================

  return (
    <Form {...form}>
      <form
        id={id}
        onSubmit={form.handleSubmit(handleSubmit)}
        className="space-y-6"
      >
        {/* Customer */}
        <FormField
          control={form.control}
          name="customerId"
          render={({ field }) => (
            <FormItem>
              <FormLabel required>Khách hàng</FormLabel>
              <FormControl>
                <Combobox
                  options={customerOptions.map((c) => ({
                    value: c.id,
                    label: c.name,
                    description: c.phone,
                  }))}
                  value={field.value}
                  onChange={field.onChange}
                  onSearch={setCustomerSearch}
                  placeholder={appointment?.customerName || "Tìm khách hàng..."}
                  searchPlaceholder="Nhập tên hoặc SĐT..."
                  emptyMessage="Nhập 2 ký tự để tìm..."
                  isLoading={isSearching}
                />
              </FormControl>
              <FormMessage />
            </FormItem>
          )}
        />

        {/* Services */}
        <FormField
          control={form.control}
          name="serviceIds"
          render={({ field }) => (
            <FormItem>
              <FormLabel required>Dịch vụ trị liệu</FormLabel>
              <FormControl>
                <MultiServiceSelector
                  selectedIds={field.value}
                  onChange={field.onChange}
                  availableServices={availableServices}
                />
              </FormControl>
              <FormDescription>
                Tổng thời lượng: {totalDuration} phút
              </FormDescription>
              <FormMessage />
            </FormItem>
          )}
        />

        {/* Staff */}
        <FormField
          control={form.control}
          name="staffId"
          render={({ field }) => (
            <FormItem>
              <FormLabel required>Kỹ thuật viên</FormLabel>
              <Select value={field.value} onValueChange={field.onChange}>
                <FormControl>
                  <SelectTrigger>
                    <SelectValue placeholder="Chọn kỹ thuật viên" />
                  </SelectTrigger>
                </FormControl>
                <SelectContent>
                  {availableStaff.map((s) => (
                    <SelectItem key={s.id} value={s.id}>
                      <div className="flex items-center gap-2">
                        <div
                          className="h-3 w-3 rounded-full"
                          style={{ backgroundColor: s.color || "#8b5cf6" }}
                        />
                        {s.name}
                      </div>
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
              <FormMessage />
            </FormItem>
          )}
        />

        {/* Date & Time */}
        <div className="grid grid-cols-2 gap-4">
          <FormField
            control={form.control}
            name="date"
            render={({ field }) => (
              <FormItem>
                <FormLabel required>Ngày</FormLabel>
                <FormControl>
                  <DatePicker
                    value={field.value}
                    onChange={field.onChange}
                    placeholder="Chọn ngày"
                    minDate={new Date()}
                  />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          <FormField
            control={form.control}
            name="startTime"
            render={({ field }) => (
              <FormItem>
                <FormLabel required>Giờ bắt đầu</FormLabel>
                <FormControl>
                  <TimePicker value={field.value} onChange={field.onChange} />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          {/* Warnings */}
          {(timeWarning || conflicts.length > 0) && (
            <div className="col-span-2 space-y-1 rounded-md border border-amber-200 bg-amber-50 p-3 text-sm dark:border-amber-800 dark:bg-amber-950/20">
              {timeWarning && (
                <div className="flex items-center gap-2 font-medium text-amber-800 dark:text-amber-500">
                  <Icon icon={AlertTriangle} /> {timeWarning}
                </div>
              )}
              {conflicts.map((c) => (
                <div
                  key={c.eventId}
                  className="text-destructive flex items-center gap-2 font-medium"
                >
                  <Icon icon={AlertTriangle} /> {c.message}
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Resources */}
        <FormField
          control={form.control}
          name="resourceId"
          render={({ field }) => (
            <FormItem>
              <FormLabel>Phòng / Giường</FormLabel>
              <Select
                value={field.value || ""}
                onValueChange={(value) => field.onChange(value || undefined)}
              >
                <FormControl>
                  <SelectTrigger>
                    <SelectValue placeholder="Chọn phòng (không bắt buộc)" />
                  </SelectTrigger>
                </FormControl>
                <SelectContent>
                  {availableResources.map((resource) => (
                    <SelectItem key={resource.id} value={resource.id}>
                      {resource.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
              <FormMessage />
            </FormItem>
          )}
        />

        {/* Notes */}
        <FormField
          control={form.control}
          name="notes"
          render={({ field }) => (
            <FormItem>
              <FormLabel>Ghi chú</FormLabel>
              <FormControl>
                <Textarea
                  placeholder="Ghi chú cho lịch hẹn..."
                  className="min-h-20 resize-none"
                  rows={3}
                  {...field}
                />
              </FormControl>
              <FormMessage />
            </FormItem>
          )}
        />
      </form>
    </Form>
  );
}

"use client";

import { showToast } from "@/shared/ui/sonner";
import { differenceInHours, format } from "date-fns";
import { vi } from "date-fns/locale";
import { AlertTriangle, Calendar, Clock, User, XCircle } from "lucide-react";
import { useState, useTransition } from "react";

import { cn } from "@/shared/lib/utils";
import { Button } from "@/shared/ui/button";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/shared/ui/dialog";
import { Textarea } from "@/shared/ui/textarea";

import type { CalendarEvent } from "../../model/types";

interface CancelDialogProps {
  event: CalendarEvent | null;
  open: boolean;
  onOpenChange: (open: boolean) => void;
  onConfirm: (event: CalendarEvent, reason?: string) => void;
  isPending?: boolean;
}

const CANCELLATION_POLICY = {
  freeHours: 2,
  lateFee: 50,
};

export function CancelDialog({
  event,
  open,
  onOpenChange,
  onConfirm,
  isPending = false,
}: CancelDialogProps) {
  const [reason, setReason] = useState("");

  if (!event) return null;

  const hoursUntilStart = differenceInHours(event.start, new Date());
  const isLateCancel = hoursUntilStart < CANCELLATION_POLICY.freeHours;
  const timeRange = `${format(event.start, "HH:mm")} - ${format(event.end, "HH:mm")}`;
  const dateStr = format(event.start, "EEEE, d MMMM yyyy", { locale: vi });

  const handleCancel = () => {
    onConfirm(event, reason || undefined);
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="sm:max-w-md">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <XCircle className="text-warning h-5 w-5" />
            Xác nhận hủy lịch hẹn
          </DialogTitle>
          <DialogDescription>
            Bạn có chắc chắn muốn hủy lịch hẹn này?
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-3 py-4">
          {/* Service & Customer */}
          <div className="bg-muted/50 space-y-2 rounded-lg p-3">
            <div
              className="text-base font-semibold"
              style={{ color: event.color }}
            >
              {event.appointment.serviceName}
            </div>
            <div className="flex items-center gap-2 text-sm">
              <User className="text-muted-foreground size-4" />
              <span>{event.appointment.customerName}</span>
            </div>
            <div className="text-muted-foreground flex items-center gap-2 text-sm">
              <Calendar className="size-4" />
              <span>{dateStr}</span>
            </div>
            <div className="text-muted-foreground flex items-center gap-2 text-sm">
              <Clock className="size-4" />
              <span>{timeRange}</span>
            </div>
          </div>

          <div className="space-y-2 rounded-lg border p-3">
            <div className="text-sm font-medium">📋 Chính sách hủy:</div>
            <ul className="text-muted-foreground ml-4 space-y-1 text-sm">
              <li>
                • Hủy trước {CANCELLATION_POLICY.freeHours} giờ:{" "}
                <span className="text-success font-medium">Miễn phí</span>
              </li>
              <li>
                • Hủy trong {CANCELLATION_POLICY.freeHours} giờ:{" "}
                <span className="text-warning font-medium">
                  Phí {CANCELLATION_POLICY.lateFee}%
                </span>
              </li>
            </ul>
          </div>

          {isLateCancel && (
            <div
              className={cn(
                "flex items-start gap-3 rounded-lg p-3",
                "bg-warning/10 border-warning/20 border"
              )}
            >
              <AlertTriangle className="text-warning mt-0.5 h-5 w-5 flex-shrink-0" />
              <div className="text-sm">
                <div className="text-warning-foreground font-medium">
                  Cảnh báo: Hủy sát giờ
                </div>
                <div className="text-muted-foreground">
                  Bạn đang hủy trong vòng {CANCELLATION_POLICY.freeHours} giờ
                  trước giờ hẹn.
                  {hoursUntilStart > 0
                    ? ` Còn ${hoursUntilStart} giờ nữa là đến giờ hẹn.`
                    : " Lịch hẹn đã quá giờ."}
                </div>
              </div>
            </div>
          )}

          <div className="space-y-2">
            <label className="text-sm font-medium">
              Lý do hủy{" "}
              <span className="text-muted-foreground font-normal">
                (tùy chọn)
              </span>
            </label>
            <Textarea
              placeholder="Nhập lý do hủy lịch hẹn..."
              value={reason}
              onChange={(e) => setReason(e.target.value)}
              rows={3}
              disabled={isPending}
            />
          </div>
        </div>

        <DialogFooter className="gap-2 sm:gap-0">
          <Button
            variant="outline"
            onClick={() => onOpenChange(false)}
            disabled={isPending}
          >
            Quay lại
          </Button>
          <Button
            variant="destructive"
            onClick={handleCancel}
            disabled={isPending}
            isLoading={isPending}
          >
            {isPending ? "Đang hủy..." : "Xác nhận hủy"}
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
}

"use client";

import { createAppointment } from "@/features/appointments/actions";
import { AppointmentForm } from "@/features/appointments/components/sheet/appointment-form";
import { MockService } from "@/features/appointments/model/mocks";
import {
  Appointment,
  TimelineResource,
} from "@/features/appointments/model/types";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/shared/ui/dialog";
import { showToast } from "@/shared/ui/sonner";
import { Loader2 } from "lucide-react";
import { useTransition } from "react";

interface WalkInBookingDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  availableStaff: TimelineResource[];
  availableResources: TimelineResource[];
  availableServices: MockService[];
  onBookingSuccess: () => void;
}

export function WalkInBookingDialog({
  open,
  onOpenChange,
  availableStaff,
  availableResources,
  availableServices,
  onBookingSuccess,
}: WalkInBookingDialogProps) {
  const [isPending, startTransition] = useTransition();

  const handleCreateAppointment = (appointment: Appointment) => {
    startTransition(async () => {
      const result = await createAppointment({
        customerId: appointment.customerId,
        serviceIds: appointment.items.map((item: any) => item.serviceId),
        staffId: appointment.staffId,
        resourceId: appointment.resourceId,
        startTime: appointment.startTime,
        notes: appointment.notes,
      });

      if (result.status === "success") {
        showToast.success(result.message || "Tạo lịch hẹn thành công");
        onOpenChange(false);
        onBookingSuccess();
      } else {
        showToast.error(result.message || "Không thể tạo lịch hẹn");
      }
    });
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="gap-0 p-0 sm:max-w-xl">
        <DialogHeader className="shrink-0 space-y-0 border-b px-6 py-4">
          <DialogTitle className="text-lg">
            Tạo lịch hẹn nhanh (Khách vãng lai)
          </DialogTitle>
        </DialogHeader>
        <div className="relative p-6">
          <AppointmentForm
            onSubmit={handleCreateAppointment}
            availableStaff={availableStaff}
            availableResources={availableResources}
            availableServices={availableServices}
          />
          {isPending && (
            <div className="bg-background/80 absolute inset-0 z-10 flex items-center justify-center backdrop-blur-sm">
              <Loader2 className="text-primary h-8 w-8 animate-spin" />
            </div>
          )}
        </div>
      </DialogContent>
    </Dialog>
  );
}

"use client";

import { ActionResponse } from "@/shared/lib/action-response";
import { showToast } from "@/shared/ui/sonner";
import { useCallback, useTransition } from "react";
import {
  cancelAppointment,
  checkInAppointment,
  deleteAppointment,
  markNoShow,
} from "../actions";
import type { Appointment, AppointmentStatus, CalendarEvent } from "../model/types";
import { OptimisticAction } from "./use-appointment-events";

interface UseAppointmentActionsProps {
  events: CalendarEvent[];
  refreshEvents: () => void;
  setSelectedBookingForReview: (id: string | null) => void;
  setIsSheetOpen: (open: boolean) => void;
  setIsDeleteOpen: (open: boolean) => void;
  setIsCancelOpen: (open: boolean) => void;
  addOptimisticEvent: (action: OptimisticAction) => void;

  // Dependency Injection: Actions from other features
  createInvoice: (bookingId: string) => Promise<ActionResponse<unknown>>;
  getInvoice: (bookingId: string) => Promise<ActionResponse<unknown>>;
  getBookingReview: (bookingId: string) => Promise<ActionResponse<unknown>>;
}

/**
 * Hook to manage appointment-related actions (save, delete, check-in, etc.)
 */
export function useAppointmentActions({
  events,
  refreshEvents,
  setSelectedBookingForReview,
  setIsSheetOpen,
  setIsDeleteOpen,
  setIsCancelOpen,
  addOptimisticEvent,
  createInvoice,
  getInvoice,
  getBookingReview,
}: UseAppointmentActionsProps) {
  const [isPending, startTransition] = useTransition();
  const [isDeleting, startDeleteTransition] = useTransition();
  const [isCancelling, startCancelTransition] = useTransition();

  const internalReviewNeeded = useCallback(
    async (bookingId: string) => {
      const booking = events.find((e) => e.id === bookingId)?.appointment;
      if (!booking || booking.status !== "COMPLETED") return;

      const invoiceRes = await getInvoice(bookingId);
      const invoice = invoiceRes.data as { status: string } | null;
      if (invoiceRes.status !== "success" || !invoice || invoice.status !== "PAID") {
        return;
      }

      const reviewRes = await getBookingReview(bookingId);
      if (!(reviewRes.status === "success" && reviewRes.data)) {
        setSelectedBookingForReview(bookingId);
      }
    },
    [events, setSelectedBookingForReview, getInvoice, getBookingReview]
  );

  const handleSaveAppointment = (appointment: Appointment) => {
    setIsSheetOpen(false);
    refreshEvents();
    if (appointment.status === "COMPLETED") {
      internalReviewNeeded(appointment.id);
    }
  };

  const wrapAction = useCallback(
    (
      fn: (id: string) => Promise<ActionResponse<Appointment | undefined>>,
      successMsg: string,
      errorMsg: string,
      targetStatus: AppointmentStatus
    ) => {
      return async (event: CalendarEvent) => {
        if (!event.id) return;

        // Perform optimistic update
        addOptimisticEvent({ type: "update_status", id: event.id, status: targetStatus });

        startTransition(async () => {
          const res = await fn(event.id!);
          if (res.status === "success") {
            showToast.success(res.message || successMsg);
            refreshEvents();
          } else {
            showToast.error(res.message || errorMsg);
          }
        });
      };
    },
    [refreshEvents, addOptimisticEvent]
  );

  const handleConfirmDelete = (actionEvent: CalendarEvent | null) => {
    if (!actionEvent?.id) return;

    // Optimistic delete
    addOptimisticEvent({ type: "delete", id: actionEvent.id });
    setIsDeleteOpen(false);

    startDeleteTransition(async () => {
      const res = await deleteAppointment(actionEvent.id!);
      if (res.status === "success") {
        showToast.success(res.message || "Xóa lịch hẹn thành công");
        refreshEvents();
      } else {
        showToast.error(res.message || "Không thể xóa lịch hẹn");
        // State reverts automatically here too
      }
    });
  };

  const handleConfirmCancel = (actionEvent: CalendarEvent | null, reason?: string) => {
    if (!actionEvent?.id) return;

    // Optimistic cancel
    addOptimisticEvent({ type: "update_status", id: actionEvent.id, status: "CANCELLED" });
    setIsCancelOpen(false);

    startCancelTransition(async () => {
      const res = await cancelAppointment(actionEvent.id!, reason);
      if (res.status === "success") {
        showToast.success(res.message || "Đã hủy lịch hẹn");
        refreshEvents();
      } else {
        showToast.error(res.message || "Không thể hủy lịch hẹn");
      }
    });
  };

  const handleCreateInvoice = useCallback(async (bookingId: string) => {
    const res = await createInvoice(bookingId);
    if (res.status === "success" && res.data) {
      showToast.success(res.message || "Tạo hóa đơn thành công");
      return res.data as any; // Cast to any for DI compatibility
    }
    showToast.error(res.message || "Không thể tạo hóa đơn");
    return null;
  }, [createInvoice]);


  return {
    isPending,
    isDeleting,
    isCancelling,
    handleSaveAppointment,
    handleConfirmDelete,
    handleConfirmCancel,
    handleCreateInvoice,
    handleReviewNeeded: internalReviewNeeded,
    onCheckIn: wrapAction(checkInAppointment, "Check-in thành công", "Không thể check-in", "IN_PROGRESS"),
    onNoShow: wrapAction(markNoShow, "Đã đánh dấu No-show", "Không thể đánh dấu No-show", "NO_SHOW"),
  };
}

"use client";

import { useEffect, useState, useTransition } from "react";
import { getAppointmentMetrics } from "../actions";
import type { AppointmentMetrics } from "../model/types";

/**
 * Hook to manage appointment metrics fetching and state.
 * Encapsulates the metric-related logic from the appointments page.
 */
export function useAppointmentMetrics(date: Date) {
  const [metrics, setMetrics] = useState<AppointmentMetrics | null>(null);
  const [isPending, startTransition] = useTransition();

  const fetchMetrics = () => {
    startTransition(async () => {
      const res = await getAppointmentMetrics(date);
      if (res.status === "success" && res.data) {
        setMetrics(res.data);
      }
    });
  };

  useEffect(() => {
    fetchMetrics();
  }, [date]);

  return {
    metrics,
    isPending,
    refreshMetrics: fetchMetrics,
  };
}

"use server";

import { MOCK_AUDIT_LOGS } from "./model/mocks";
import { AuditLog, PaginatedAuditLogs } from "./model/types";

export async function getAuditLogs(
  page = 1,
  pageSize = 10,
  filter?: { action?: string; entityType?: string } // simplified filter
): Promise<PaginatedAuditLogs> {
  // Simulate network delay
  await new Promise((resolve) => setTimeout(resolve, 500));

  let filtered = [...MOCK_AUDIT_LOGS];

  if (filter?.action && filter.action !== "all") {
    filtered = filtered.filter((log) => log.action === filter.action);
  }

  if (filter?.entityType && filter.entityType !== "all") {
    filtered = filtered.filter((log) => log.entity_type === filter.entityType);
  }

  // Sort by created_at desc
  filtered.sort(
    (a, b) =>
      new Date(b.created_at).getTime() - new Date(a.created_at).getTime()
  );

  const total = filtered.length;
  const start = (page - 1) * pageSize;
  const end = start + pageSize;
  const data = filtered.slice(start, end);
  const totalPages = Math.ceil(total / pageSize);

  return {
    data,
    total,
    page,
    pageSize,
    totalPages,
  };
}

