"use client";

import { Resource, RoomType } from "@/features/resources";
import { PageFooter } from "@/shared/components/layout/components/page-footer";
import {
  PageContent,
  PageHeader,
  PageShell,
  SurfaceCard,
} from "@/shared/components/layout/page-layout";
import { FilterBar } from "@/shared/ui/custom/filter-bar";
import { SearchInput } from "@/shared/ui/custom/search-input";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/shared/ui/tabs";
import { ActionResponse } from "@/shared/lib/action-response";
import { usePathname, useRouter, useSearchParams } from "next/navigation";
import { Suspense, use, useState, useTransition } from "react";
import { useDebouncedCallback } from "use-debounce";
import { PaginatedResponse, Service, Skill } from "../model/types";
import { CreateServiceWizard } from "./create-service-wizard";
import { CreateSkillDialog } from "./create-skill-dialog";
import { ServiceFilter } from "./service-filter";
import { ServiceTable, ServiceTableSkeleton } from "./service-table";
import { SkillTable } from "./skill-table";

interface ServicesPageProps {
  page: number;
  skills: Skill[];
  roomTypes: RoomType[];
  equipmentList: Resource[];
  servicesPromise: Promise<ActionResponse<PaginatedResponse<Service>>>;
}

function ServiceListWrapper({
  servicesPromise,
  skills,
  roomTypes,
  equipmentList,
  page,
}: {
  servicesPromise: Promise<ActionResponse<PaginatedResponse<Service>>>;
  skills: Skill[];
  roomTypes: RoomType[];
  equipmentList: Resource[];
  page: number;
}) {
  const servicesRes = use(servicesPromise);

  const { data, total } =
    servicesRes.status === "success" && servicesRes.data
      ? servicesRes.data
      : { data: [], total: 0 };

  const totalPages = Math.ceil(total / 10);

  if (servicesRes.status === "error") {
    return (
      <div className="text-destructive p-4">
        Lỗi tải dịch vụ: {servicesRes.message}
      </div>
    );
  }

  return (
    <ServiceTable
      services={data}
      availableSkills={skills}
      availableRoomTypes={roomTypes}
      availableEquipment={equipmentList}
      page={page}
      totalPages={totalPages}
      variant="flush"
      className="border-none"
      hidePagination={true}
    />
  );
}

function ServicesFooterWrapper({
  servicesPromise,
  page,
  pathname,
  searchParams,
}: {
  servicesPromise: Promise<ActionResponse<PaginatedResponse<Service>>>;
  page: number;
  pathname: string;
  searchParams: URLSearchParams;
}) {
  const router = useRouter();
  const [, startTransition] = useTransition();
  const response = use(servicesPromise);
  const total =
    response.status === "success" && response.data ? response.data.total : 0;
  const totalPages = Math.ceil(total / 10);

  const handlePageChange = (p: number) => {
    const params = new URLSearchParams(searchParams);
    params.set("page", p.toString());
    startTransition(() => {
      router.push(`${pathname}?${params.toString()}`);
    });
  };

  return (
    <PageFooter
      page={page}
      totalPages={totalPages}
      onPageChange={handlePageChange}
    />
  );
}

export function ServicesPage({
  page,
  skills,
  roomTypes,
  equipmentList,
  servicesPromise,
}: ServicesPageProps) {
  const router = useRouter();
  const pathname = usePathname();
  const searchParams = useSearchParams();
  const [, startTransition] = useTransition();

  // Get active tab from URL or default to 'list'
  const activeTab = searchParams.get("view") || "list";

  // Get initial search query from URL
  const initialSearch = searchParams.get("search")?.toString() || "";

  const handleTabChange = (value: string) => {
    const params = new URLSearchParams(searchParams);
    params.set("view", value);
    startTransition(() => {
      router.push(`${pathname}?${params.toString()}`);
    });
  };

  // Debounced search handler - syncs with URL params
  const handleSearch = useDebouncedCallback((term: string) => {
    const params = new URLSearchParams(searchParams);
    if (term) {
      params.set("search", term);
    } else {
      params.delete("search");
    }
    // Reset page to 1 when searching
    params.set("page", "1");

    startTransition(() => {
      router.replace(`${pathname}?${params.toString()}`);
    });
  }, 300);

  const isServiceTab = activeTab === "list";

  return (
    <PageShell>
      <Tabs
        value={activeTab}
        className="flex w-full flex-1 flex-col gap-0"
        onValueChange={handleTabChange}
      >
        <PageHeader>
          <TabsList variant="default" size="default" aria-label="Quản lý dịch vụ">
            <TabsTrigger
              value="list"
              aria-label="Danh sách dịch vụ"
              variant="default"
              stretch={false}
            >
              Dịch vụ
            </TabsTrigger>
            <TabsTrigger
              value="skills"
              aria-label="Danh sách kỹ năng"
              variant="default"
              stretch={false}
            >
              Kỹ năng
            </TabsTrigger>
          </TabsList>

          <div className="flex w-full items-center gap-3 md:w-auto">
            <FilterBar
              startContent={
                <SearchInput
                  placeholder={
                    isServiceTab ? "Tìm kiếm dịch vụ..." : "Tìm kiếm kỹ năng..."
                  }
                  defaultValue={initialSearch}
                  onChange={(e) => handleSearch(e.target.value)}
                />
              }
              endContent={
                isServiceTab && <ServiceFilter availableSkills={skills} />
              }
            />

            {isServiceTab ? (
              <CreateServiceWizard
                availableSkills={skills}
                availableRoomTypes={roomTypes}
                availableEquipment={equipmentList}
              />
            ) : (
              <CreateSkillDialog />
            )}
          </div>
        </PageHeader>

        <div className="page-entry-animation flex flex-1 flex-col overflow-hidden">
          <TabsContent
            value="list"
            className="mt-0 flex flex-1 flex-col border-0 p-0 data-[state=inactive]:hidden"
          >
            <PageContent>
              <SurfaceCard>
                <Suspense fallback={<ServiceTableSkeleton />}>
                  <ServiceListWrapper
                    servicesPromise={servicesPromise}
                    skills={skills}
                    roomTypes={roomTypes}
                    equipmentList={equipmentList}
                    page={page}
                  />
                </Suspense>
              </SurfaceCard>
              <Suspense fallback={<PageFooter />}>
                <ServicesFooterWrapper
                  servicesPromise={servicesPromise}
                  page={page}
                  pathname={pathname}
                  searchParams={searchParams}
                />
              </Suspense>
            </PageContent>
          </TabsContent>

          <TabsContent
            value="skills"
            className="mt-0 flex flex-1 flex-col border-0 p-0 data-[state=inactive]:hidden"
          >
            <PageContent>
              <SurfaceCard>
                <SkillTable skills={skills} className="border-none" />
              </SurfaceCard>
              <PageFooter />
            </PageContent>
          </TabsContent>
        </div>
      </Tabs>
    </PageShell>
  );
}

"use client";

import { SurfaceCard } from "@/shared/components/layout/page-layout";
import { CardContent } from "@/shared/ui/card";
import { useState, useTransition } from "react";
import { toast } from "sonner";
import {
  toggleChannelAction,
  updateChannelConfigAction,
  updateTemplateAction,
} from "../notifications/actions";
import { ChannelConfigDialog } from "../notifications/components/channel-config-dialog";
import { NotificationChannels } from "../notifications/components/notification-channels";
import { NotificationList } from "../notifications/components/notification-list";
import { TemplateEditor } from "../notifications/components/template-editor";
import { NotificationChannel, NotificationEvent } from "../notifications";

interface NotificationsSettingsProps {
  initialChannels: NotificationChannel[];
  initialEvents: NotificationEvent[];
}

export function NotificationsSettings({
  initialChannels,
  initialEvents,
}: NotificationsSettingsProps) {
  const [channels, setChannels] = useState(initialChannels);
  const [events, setEvents] = useState(initialEvents);
  const [isPending, startTransition] = useTransition();

  const [configuringChannelId, setConfiguringChannelId] = useState<
    string | null
  >(null);
  const [editingTemplate, setEditingTemplate] = useState<{
    eventId: string;
    channelId: string;
  } | null>(null);

  const handleToggleChannel = (
    eventId: string,
    channelId: string,
    checked: boolean
  ) => {
    // Optimistic update
    setEvents((prev) =>
      prev.map((event) => {
        if (event.id === eventId) {
          return {
            ...event,
            channels: {
              ...event.channels,
              [channelId]: checked,
            },
          };
        }
        return event;
      })
    );

    startTransition(async () => {
      try {
        const result = await toggleChannelAction(eventId, channelId, checked);
        if (result.status !== "success") {
          throw new Error(result.message);
        }
      } catch {
        toast.error("Không thể cập nhật cấu hình");
        // Revert state on error
        setEvents((prev) =>
          prev.map((event) => {
            if (event.id === eventId) {
              return {
                ...event,
                channels: {
                  ...event.channels,
                  [channelId]: !checked,
                },
              };
            }
            return event;
          })
        );
      }
    });
  };

  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  const handleSaveChannelConfig = (channelId: string, config: any) => {
    startTransition(async () => {
      const result = await updateChannelConfigAction(channelId, config);
      if (result.status === "success") {
        setChannels((prev) =>
          prev.map((ch) => {
            if (ch.id === channelId) {
              return { ...ch, config: { ...ch.config, ...config } };
            }
            return ch;
          })
        );
        setConfiguringChannelId(null);
        toast.success(result.message);
      } else {
        toast.error("Lỗi khi lưu cấu hình");
      }
    });
  };

  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  const handleSaveTemplate = (
    eventId: string,
    channelId: string,
    template: any
  ) => {
    startTransition(async () => {
      const result = await updateTemplateAction(eventId, channelId, template);
      if (result.status === "success") {
        setEvents((prev) =>
          prev.map((event) => {
            if (event.id === eventId) {
              return {
                ...event,
                templates: {
                  ...event.templates,
                  [channelId]: template,
                },
              };
            }
            return event;
          })
        );
        setEditingTemplate(null);
        toast.success(result.message);
      } else {
        toast.error("Lỗi khi lưu mẫu tin");
      }
    });
  };

  const activeChannelConfig = channels.find(
    (c) => c.id === configuringChannelId
  );

  const activeTemplateEvent = editingTemplate
    ? events.find((e) => e.id === editingTemplate.eventId)
    : null;
  const activeTemplate =
    activeTemplateEvent && editingTemplate
      ? activeTemplateEvent.templates[
          editingTemplate.channelId as keyof typeof activeTemplateEvent.templates
        ]
      : undefined;

  return (
    <div className="space-y-6">
      {/* Channels Configuration */}
      <SurfaceCard>
        <CardContent>
          <NotificationChannels
            channels={channels}
            onConfigure={setConfiguringChannelId}
          />
        </CardContent>
      </SurfaceCard>

      {/* Events Configuration */}
      <SurfaceCard>
        <CardContent>
          <NotificationList
            events={events}
            onToggleChannel={handleToggleChannel}
            onEditTemplate={(eventId, channelId) =>
              setEditingTemplate({ eventId, channelId })
            }
          />
        </CardContent>
      </SurfaceCard>

      {/* Dialogs */}
      {activeChannelConfig && (
        <ChannelConfigDialog
          isOpen={!!configuringChannelId}
          onOpenChange={(open) =>
            !open && !isPending && setConfiguringChannelId(null)
          }
          channel={activeChannelConfig}
          onSave={(channelId, config) =>
            handleSaveChannelConfig(channelId, config)
          }
          isSaving={isPending}
        />
      )}

      {editingTemplate && activeTemplate && activeTemplateEvent && (
        <TemplateEditor
          isOpen={!!editingTemplate}
          onOpenChange={(open) =>
            !open && !isPending && setEditingTemplate(null)
          }
          title={`${activeTemplateEvent.name} - ${editingTemplate.channelId.toUpperCase()}`}
          template={activeTemplate}
          onSave={(template) =>
            handleSaveTemplate(
              activeTemplateEvent.id,
              editingTemplate.channelId,
              template
            )
          }
          isSaving={isPending}
        />
      )}
    </div>
  );
}

