                </Badge>
              </div>
              <Progress value={data.progress} className="h-3" />
            </div>
          </div>

          {/* Times */}
          <div className="grid grid-cols-2 gap-4 pb-6">
            <div className="space-y-1">
              <p className="text-muted-foreground flex items-center gap-1 text-xs">
                <Calendar className="size-3" /> Ngày bắt đầu
              </p>
              <p className="text-sm font-medium">
                {format(new Date(data.start_date), "dd/MM/yyyy", {
                  locale: vi,
                })}
              </p>
            </div>
            <div className="space-y-1">
              <p className="text-muted-foreground flex items-center gap-1 text-xs">
                <Calendar className="size-3" /> Hết hạn
              </p>
              <p className="text-sm font-medium">
                {format(new Date(data.expiry_date), "dd/MM/yyyy", {
                  locale: vi,
                })}
              </p>
            </div>
          </div>

          {/* Notes Section */}
          <div className="space-y-3 border-t pt-4">
            <div className="flex items-center justify-between">
              <h3 className="flex items-center gap-2 text-sm font-medium">
                <StickyNote className="size-4" /> Ghi chú chuyên môn
              </h3>
              {!isEditing && (
                <Button
                  variant="ghost"
                  size="sm"
                  className="h-8 gap-1.5 text-xs"
                  onClick={() => setIsEditing(true)}
                >
                  <Edit3 className="size-3" /> Chỉnh sửa
                </Button>
              )}
            </div>

            {isEditing ? (
              <div className="space-y-3">
                <Textarea
                  value={editedNotes}
                  onChange={(e) => setEditedNotes(e.target.value)}
                  placeholder="Nhập ghi chú chi tiết về tình trạng da, phản ứng của khách..."
                  className="min-h-[120px] text-sm leading-relaxed"
                />
                <div className="flex items-center gap-2">
                  <Button
                    size="sm"
                    className="flex-1 gap-2"
                    onClick={handleSaveNotes}
                  >
                    <Save className="size-4" /> Lưu ghi chú
                  </Button>
                  <Button
                    size="sm"
                    variant="outline"
                    onClick={() => setIsEditing(false)}
                  >
                    <X className="size-4" /> Hủy
                  </Button>
                </div>
              </div>
            ) : (
              <div className="text-muted-foreground rounded-xl border border-amber-200/50 bg-amber-50/50 p-4 text-sm italic leading-relaxed dark:border-amber-900/30 dark:bg-amber-900/10">
                {data.notes
                  ? `"${data.notes}"`
                  : "Chưa có ghi chú chuyên môn cho liệu trình này."}
              </div>
            )}
          </div>
        </div>
      </SheetContent>
    </Sheet>
  );
}

"use client";

import { useSheetForm } from "@/shared/hooks/use-sheet-form";
import { Button } from "@/shared/ui/button";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
  Sheet,
  SheetContent,
  SheetFooter,
  SheetHeader,
  SheetTitle,
} from "@/shared/ui";
import { Input } from "@/shared/ui/input";
import { Textarea } from "@/shared/ui/textarea";
import { createWaitlistEntry, updateWaitlistEntry } from "../actions";
import {
  waitlistCreateSchema,
  type WaitlistFormValues,
} from "../model/schemas";
import { WaitlistEntry } from "../model/types";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/shared/ui/select";
import { MOCK_SERVICES } from "@/features/services/model/mocks"; // Import mock services for dropdown

interface WaitlistSheetProps {
  mode: "create" | "edit";
  open: boolean;
  onOpenChange: (open: boolean) => void;
  data?: WaitlistEntry;
  defaultValues?: Partial<WaitlistFormValues>;
}

export function WaitlistSheet({
  mode,
  open,
  onOpenChange,
  data,
  defaultValues,
}: WaitlistSheetProps) {
  const isCreate = mode === "create";

  const { form, isPending, onSubmit } = useSheetForm<
    WaitlistFormValues,
    WaitlistEntry
  >({
    schema: waitlistCreateSchema,
    defaultValues: {
      customer_id: "",
      customer_name: "",
      phone_number: "",
      service_id: "",
      preferred_date: new Date().toISOString().split("T")[0],
      preferred_time_slot: "",
      notes: "",
      ...defaultValues, // Allow overrides
    },
    open,
    data,
    transformData: (entry) => ({
      ...entry,
      customer_id: entry.customer_id, // Ensure all fields mapped
    }),
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    action: (mode === "create"
      ? createWaitlistEntry
      : updateWaitlistEntry) as any,
    onSuccess: () => onOpenChange(false),
    toastMessages: {
      success: isCreate ? "Đã thêm vào danh sách chờ" : "Đã cập nhật yêu cầu",
    },
  });

  // Removed useEffect reset logic as hook handles it

  // The onSubmit function is now returned by useSheetForm and handles the action.
  // The local onSubmit logic is integrated into the useSheetForm hook's behavior.
  // For edit mode, you would typically pass an update action to useSheetForm.
  // Since the instruction only shows the create path for the action,
  // and the original code had a placeholder for edit, we'll keep the edit placeholder
  // but note that `onSubmit` from `useSheetForm` is primarily for the `action` provided.
  // onSubmit is handled by hook
  // const onSubmit = ... removed

  return (
    <Sheet open={open} onOpenChange={onOpenChange}>
      <SheetContent className="bg-background flex w-full flex-col gap-0 border-l p-0 shadow-2xl sm:max-w-lg">
        <SheetHeader className="shrink-0 space-y-0 border-b px-6 py-4">
          <SheetTitle className="text-foreground text-lg font-semibold">
            {isCreate ? "Thêm vào danh sách chờ" : "Chi tiết yêu cầu"}
          </SheetTitle>
        </SheetHeader>

        <div className="sheet-scroll-area">

        <Form {...form}>
          <form
            onSubmit={onSubmit}
            className="mt-6 flex flex-1 flex-col gap-6 overflow-y-auto px-1"
          >
            <div className="space-y-4">
              <FormField
                control={form.control}
                name="customer_name"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel required>Tên khách hàng</FormLabel>
                    <FormControl>
                      <Input placeholder="Nhập tên khách hàng" {...field} />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="phone_number"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel required>Số điện thoại</FormLabel>
                    <FormControl>
                      <Input placeholder="09xxxx..." {...field} />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="service_id"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel required>Dịch vụ quan tâm</FormLabel>
                    <Select
                      onValueChange={field.onChange}
                      defaultValue={field.value}
                    >
                      <FormControl>
                        <SelectTrigger>
                          <SelectValue placeholder="Chọn dịch vụ" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        {MOCK_SERVICES.map((s) => (
                          <SelectItem key={s.id} value={s.id}>
                            {s.name}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <div className="grid grid-cols-2 gap-4">
                <FormField
                  control={form.control}
                  name="preferred_date"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Ngày mong muốn</FormLabel>
                      <FormControl>
                        <Input type="date" {...field} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <FormField
                  control={form.control}
                  name="preferred_time_slot"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Khung giờ</FormLabel>
                      <Select
                        onValueChange={field.onChange}
                        defaultValue={field.value}
                      >
                        <FormControl>
                          <SelectTrigger>
                            <SelectValue placeholder="Chọn khung giờ" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          <SelectItem value="Buổi sáng (9:00 - 12:00)">
                            Buổi sáng
                          </SelectItem>
                          <SelectItem value="Buổi chiều (13:00 - 17:00)">
                            Buổi chiều
                          </SelectItem>
                          <SelectItem value="Buổi tối (17:00 - 21:00)">
                            Buổi tối
                          </SelectItem>
                          <SelectItem value="Sau 17:00">Sau 17:00</SelectItem>
                          <SelectItem value="Cuối tuần">Cuối tuần</SelectItem>
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              </div>

              <FormField
                control={form.control}
                name="notes"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Ghi chú</FormLabel>
                    <FormControl>
                      <Textarea
                        placeholder="Ghi chú thêm..."
                        className="min-h-[100px]"
                        {...field}
                        value={field.value || ""}
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <SheetFooter className="mt-auto shrink-0 border-t p-6">
              <Button
                type="button"
                variant="outline"
                onClick={() => onOpenChange(false)}
                disabled={isPending}
              >
                Hủy bỏ
              </Button>
              <Button type="submit" isLoading={isPending} disabled={isPending}>
                {isCreate ? "Gửi yêu cầu" : "Lưu thay đổi"}
              </Button>
            </SheetFooter>
          </form>
        </Form>
        </div>
      </SheetContent>
    </Sheet>
  );
}

"use client";

import { useTableParams } from "@/shared/hooks";
import { Badge } from "@/shared/ui/badge";
import { Button } from "@/shared/ui/button";
import { Column, DataTable } from "@/shared/ui/custom/data-table";
import { DataTableEmptyState } from "@/shared/ui/custom/data-table-empty-state";
import { showToast } from "@/shared/ui";
import { format } from "date-fns";
import { vi } from "date-fns/locale";
import {
  CalendarClock,
  CheckCircle2,
  Clock,
  MoreHorizontal,
  Phone,
  XCircle,
} from "lucide-react";
import { useRouter } from "next/navigation";
import { useState, useTransition } from "react";
import { updateWaitlistStatus, deleteWaitlistEntry } from "../actions";
import { WaitlistEntry } from "../model/types";
import { WaitlistSheet } from "./waitlist-sheet";
import { Icon } from "@/shared/ui/custom/icon";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/shared/ui/dropdown-menu";

interface WaitlistTableProps {
  data: WaitlistEntry[];
  page?: number;
  totalPages?: number;
  onPageChange?: (page: number) => void;
  className?: string;
  isLoading?: boolean;
  hidePagination?: boolean;
}

export function WaitlistTable({
  data,
  page: pageProp,
  totalPages = 1,
  onPageChange: onPageChangeProp,
  className,
  isLoading,
  hidePagination,
}: WaitlistTableProps) {
  const router = useRouter();
  const [editingEntry, setEditingEntry] = useState<WaitlistEntry | null>(null);
  const [, startTransition] = useTransition();

  const {
    page: urlPage,
    sortBy,
    order,
    handlePageChange: urlPageChange,
    handleSort,
  } = useTableParams({
    defaultSortBy: "created_at",
    defaultOrder: "desc",
  });

  const page = pageProp ?? urlPage;
  const handlePageChange = onPageChangeProp ?? urlPageChange;

  const handleStatusUpdate = (id: string, status: WaitlistEntry["status"]) => {
    startTransition(async () => {
      const result = await updateWaitlistStatus(id, status);
      if (result.status === "success") {
        showToast.success("Thành công", `Đã cập nhật trạng thái yêu cầu`);
        router.refresh();
      } else {
        showToast.error("Thất bại", result.message);
      }
    });
  };

  const handleDelete = (id: string) => {
    if (!confirm("Bạn có chắc chắn muốn xóa yêu cầu này?")) return;
    startTransition(async () => {
      const result = await deleteWaitlistEntry(id);
      if (result.status === "success") {
        showToast.success("Thành công", "Đã xóa yêu cầu khỏi danh sách");
        router.refresh();
      } else {
        showToast.error("Thất bại", result.message);
      }
    });
  };

  const columns: Column<WaitlistEntry>[] = [
    {
      header: "Khách hàng",
      accessorKey: "customer_name",
      cell: (w) => (
        <div className="flex flex-col">
          <span className="font-medium text-foreground">{w.customer_name}</span>
          <div className="text-muted-foreground flex items-center gap-1.5 text-xs">
            <Icon icon={Phone} size="xs" /> {w.phone_number}
          </div>
        </div>
      ),
    },
    {
      header: "Dịch vụ quan tâm",
      accessorKey: "service_name",
      cell: (w) => <div className="text-sm">{w.service_name}</div>,
    },
    {
      header: "Thời gian mong muốn",
      accessorKey: "preferred_date",
      sortable: true,
      cell: (w) => (
        <div className="flex flex-col text-sm">
          <span>
            {format(new Date(w.preferred_date), "dd/MM/yyyy", { locale: vi })}
          </span>
          <span className="text-muted-foreground text-xs">
            {w.preferred_time_slot}
          </span>
        </div>
      ),
    },
    {
      header: "Trạng thái",
      accessorKey: "status",
      cell: (w) => {
        const variants: Record<
          string,
          | "default"
          | "secondary"
          | "destructive"
          | "outline"
          | "success"
          | "warning"
        > = {
          pending: "warning",
          notified: "secondary", // Fixed invalid 'blue'
          converted: "success",
          cancelled: "destructive",
          expired: "secondary",
        };
        const labels: Record<string, string> = {
          pending: "Đang chờ",
          notified: "Đã thông báo",
          converted: "Đã đặt lịch",
          cancelled: "Đã hủy",
          expired: "Hết hạn",
        };
        const presets: Record<string, any> = {
          pending: "appointment-pending",
          notified: "appointment-confirmed",
          converted: "appointment-completed",
          cancelled: "appointment-cancelled",
          expired: "appointment-no-show",
        };
        return <Badge preset={presets[w.status]} />;
      },
    },
    {
      header: "Ghi chú",
      accessorKey: "notes",
      cell: (w) => (
        <div
          className="text-muted-foreground max-w-[200px] truncate text-sm"
          title={w.notes || ""}
        >
          {w.notes || "-"}
        </div>
      ),
    },
    {
      header: "",
      id: "actions",
      cell: (w) => (
        <div className="flex justify-end">
          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button variant="ghost" className="h-8 w-8 p-0">
                <span className="sr-only">Mở menu</span>
                <Icon icon={MoreHorizontal} className="text-muted-foreground" />
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent align="end">
              <DropdownMenuLabel>Thao tác</DropdownMenuLabel>
              <DropdownMenuItem
                onClick={() => handleStatusUpdate(w.id, "notified")}
              >
                <Icon icon={Clock} className="mr-2" size="sm" /> Đã thông báo
              </DropdownMenuItem>
              <DropdownMenuItem
                onClick={() => handleStatusUpdate(w.id, "converted")}
              >
                <Icon icon={CheckCircle2} className="mr-2" size="sm" /> Chuyển
                thành lịch hẹn
              </DropdownMenuItem>
              <DropdownMenuSeparator />
              <DropdownMenuItem
                onClick={() => handleStatusUpdate(w.id, "cancelled")}
                className="text-destructive"
              >
                <Icon icon={XCircle} className="mr-2" size="sm" /> Hủy yêu cầu
              </DropdownMenuItem>
              <DropdownMenuItem
                onClick={() => handleDelete(w.id)}
                className="text-destructive"
              >
                Xóa vĩnh viễn
              </DropdownMenuItem>
            </DropdownMenuContent>
          </DropdownMenu>
        </div>
      ),
    },
  ];

  return (
    <>
      <DataTable
        data={data}
        columns={columns}
        keyExtractor={(item) => item.id}
        page={page}
        totalPages={totalPages}
        onPageChange={handlePageChange}
        className={className}
        isLoading={isLoading}
        skeletonCount={5}
        variant="flush"
        hidePagination={hidePagination}
        // onRowClick={(w) => setEditingEntry(w)}
        sort={{
          column: sortBy,
          direction: order,
          onSort: handleSort,
        }}
        emptyState={
          <DataTableEmptyState
            icon={CalendarClock}
            title="Danh sách chờ trống"
            description="Chưa có khách hàng nào trong danh sách chờ."
            // action={<CreateWaitlistTrigger />}
          />
        }
      />

      {/* Sheet create/edit placeholder - implemented next */}
      <WaitlistSheet
        mode={editingEntry ? "edit" : "create"}
        open={!!editingEntry}
        onOpenChange={(open: boolean) => !open && setEditingEntry(null)}
        data={editingEntry || undefined}
      />
    </>
  );
}

"use client";

import { useTransition } from "react";
import { useTableParams } from "@/shared/hooks";
import { Badge } from "@/shared/ui/badge";
import { Button } from "@/shared/ui/button";
import { Column, DataTable } from "@/shared/ui/custom/data-table";
import { DataTableEmptyState } from "@/shared/ui/custom/data-table-empty-state";
import { showToast } from "@/shared/ui";
import { format } from "date-fns";
import { vi } from "date-fns/locale";
import {
  AlertTriangle,
  MoreHorizontal,
  ShieldCheck,
  XCircle,
} from "lucide-react";
import { useRouter } from "next/navigation";
import { updateWarrantyStatus } from "../actions";
import { WarrantyTicket } from "../model/types";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/shared/ui/dropdown-menu";

interface WarrantyTableProps {
  data: WarrantyTicket[];
  page?: number;
  totalPages?: number;
  onPageChange?: (page: number) => void;
  className?: string;
  isLoading?: boolean;
}

export function WarrantyTable({
  data,
  page: pageProp,
  totalPages = 1,
  onPageChange: onPageChangeProp,
  className,
  isLoading,
}: WarrantyTableProps) {
  const router = useRouter();
  const [isPending, startTransition] = useTransition();

  const {
    page: urlPage,
    sortBy,
    order,
    handlePageChange: urlPageChange,
    handleSort,
  } = useTableParams({
    defaultSortBy: "created_at",
    defaultOrder: "desc",
  });

  const page = pageProp ?? urlPage;
  const handlePageChange = onPageChangeProp ?? urlPageChange;

  const handleStatusUpdate = (id: string, status: WarrantyTicket["status"]) => {
    startTransition(async () => {
      const result = await updateWarrantyStatus(id, status);
      if (result.status === "success") {
        showToast.success("Thành công", `Đã cập nhật trạng thái: ${status}`);
        router.refresh();
      } else {
        showToast.error("Thất bại", result.message);
      }
    });
  };

  const columns: Column<WarrantyTicket>[] = [
    {
      header: "Mã bảo hành",
      accessorKey: "code",
      cell: (w) => <span className="font-mono font-medium">{w.code}</span>,
    },
    {
      header: "Khách hàng",
      accessorKey: "customer_name",
    },
    {
      header: "Dịch vụ/Liệu trình",
      accessorKey: "service_name",
      cell: (w) => (
        <div className="max-w-[200px] truncate text-sm">{w.service_name}</div>
      ),
    },
    {
      header: "Thời hạn",
      accessorKey: "end_date",
      sortable: true,
      cell: (w) => {
        const end = new Date(w.end_date);
        const now = new Date();
        const daysLeft = Math.ceil(
          (end.getTime() - now.getTime()) / (1000 * 60 * 60 * 24)
        );
        const isExpired = daysLeft < 0;

        return (
          <div className="flex flex-col text-sm">
            <span>{format(end, "dd/MM/yyyy", { locale: vi })}</span>
            <span
              className={`text-xs ${isExpired ? "text-destructive" : "text-success"}`}
            >
              {isExpired ? "Đã hết hạn" : `Còn ${daysLeft} ngày`}
            </span>
          </div>
        );
      },
    },
    {
      header: "Trạng thái",
      accessorKey: "status",
      cell: (w) => {
        const variants: Record<
          string,
          | "default"
          | "secondary"
          | "destructive"
          | "outline"
          | "success"
          | "warning"
        > = {
          active: "success",
          expired: "secondary",
          voided: "destructive",
          claimed: "warning",
        };
        const labels: Record<string, string> = {
          active: "Hiệu lực",
          expired: "Hết hạn",
          voided: "Đã hủy",
          claimed: "Đang xử lý",
        };
        return (
          <Badge variant={variants[w.status] || "outline"}>
            {labels[w.status] || w.status}
          </Badge>
        );
      },
    },
    {
      header: "",
      id: "actions",
      cell: (w) => (
        <div className="flex justify-end">
          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button variant="ghost" className="h-8 w-8 p-0">
                <span className="sr-only">Mở menu</span>
                <MoreHorizontal className="h-4 w-4" />
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent align="end">
              <DropdownMenuLabel>Thao tác</DropdownMenuLabel>
              <DropdownMenuItem
                onClick={() => handleStatusUpdate(w.id, "claimed")}
              >
                <AlertTriangle className="mr-2 h-4 w-4" /> Yêu cầu bảo hành
              </DropdownMenuItem>
              <DropdownMenuSeparator />
              <DropdownMenuItem
                onClick={() => handleStatusUpdate(w.id, "voided")}
                className="text-destructive"
              >
                <XCircle className="mr-2 h-4 w-4" /> Hủy hiệu lực
              </DropdownMenuItem>
            </DropdownMenuContent>
          </DropdownMenu>
        </div>
      ),
    },
  ];

  return (
    <>
      <DataTable
        data={data}
        columns={columns}
        keyExtractor={(item) => item.id}
        page={page}
        totalPages={totalPages}
        onPageChange={handlePageChange}
        className={className}
        isLoading={isLoading}
        skeletonCount={5}
        variant="flush"
        sort={{
          column: sortBy,
          direction: order,
          onSort: handleSort,
        }}
        emptyState={
          <DataTableEmptyState
            icon={ShieldCheck}
            title="Chưa có phiếu bảo hành"
            description="Tạo phiếu bảo hành mới cho khách hàng."
            // action={<CreateWarrantyTrigger />}
          />
        }
      />
      {/* View/Edit sheet can be added later */}
    </>
  );
}

import { cn } from "@/shared/lib/utils";
import React from "react";

// --- Page Shell ---
interface PageShellProps extends React.HTMLAttributes<HTMLDivElement> {
  animate?: boolean;
}

export function PageShell({
  className,
  animate = true,
  children,
  ...props
}: PageShellProps) {
  return (
    <div
      className={cn(
        "bg-background flex min-h-screen w-full flex-col",
        animate && "page-entry-animation",
        className
      )}
      {...props}
    >
      {children}
    </div>
  );
}

// --- Page Header ---
interface PageHeaderProps extends React.HTMLAttributes<HTMLDivElement> {
  children?: React.ReactNode;
  title?: string;
  subtitle?: string;
  backHref?: string;
}

export function PageHeader({
  className,
  children,
  title,
  subtitle,
  backHref,
  ...props
}: PageHeaderProps) {
  return (
    <header
      className={cn("header-sticky bg-card border-b z-20", className)}
      {...props}
    >
      <div className="flex h-14 items-center justify-between gap-4 px-4">
        {(title || subtitle) && (
          <div className="flex flex-col gap-0.5">
            {title && (
              <h1 className="text-lg font-semibold tracking-tight md:text-xl">
                {title}
              </h1>
            )}
            {subtitle && (
              <p className="text-muted-foreground hidden text-sm md:block">
                {subtitle}
              </p>
            )}
          </div>
        )}
        {children}
      </div>
    </header>
  );
}

// --- Page Content ---
interface PageContentProps extends React.HTMLAttributes<HTMLDivElement> {
  fullWidth?: boolean;
}

export function PageContent({
  className,
  children,
  fullWidth = false,
  ...props
}: PageContentProps) {
  return (
    <main
      className={cn(
        "flex flex-1 flex-col overflow-hidden",
        !fullWidth && "gap-4 p-4 md:p-6",
        className
      )}
      {...props}
    >
      {children}
    </main>
  );
}

// --- Surface Card ---
// Wrapper chuẩn cho content chính (Table, Form...)
type SurfaceCardProps = React.HTMLAttributes<HTMLDivElement>;

export function SurfaceCard({
  className,
  children,
  ...props
}: SurfaceCardProps) {
  return (
    <div
      className={cn(
        "surface-card flex flex-1 flex-col overflow-hidden rounded-lg",
        className
      )}
      {...props}
    >
      {children}
    </div>
  );
}

